import React, { Component } from "react";

class Form extends Component {
  constructor() {
    super();
    this.state = {
      name: "",
      email: "",
      date: ""
    };
  }
  handleChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

  handleYear = () => {
    let output = [];
    for (let i = 2019; i >= 1900; i--) {
      output.push(
        <option key={i} value="{i}">
          {i}
        </option>
      );
    }
    return output;
  };

  handleMonth = () => {
    let output = [];
    for (let i = 1; i <= 12; i++) {
      output.push(
        <option key={i} value="{i}">
          {i}
        </option>
      );
    }
  };

  handleDay = () => {
    let output = [];
    for (let i = 1; i <= 31; i++) {
      output.push(
        <option key={i} value="{i}">
          {i}
        </option>
      );
    }
  };

  render() {
    return (
      <>
        <h1> Mars Mission Regristration Form </h1>
        <form onChange={this.handleChange}>
          <label htmlFor="name"> Name </label>
          <input
            type="text"
            placeholder="Your Name"
            name="name"
            value={this.state.name}
            id="name"
          />
          <br />
          <label htmlFor="email"> Email </label>
          <input
            type="text"
            placeholder="Your Email"
            name="email"
            value={this.state.email}
            id="email"
          />
          <br />
          <select name="date_of_birth" value={this.state.date}>
            <option>Month</option>
            {this.handleMonth()}
            <option>Day</option>
            {this.handleDay()}
            <option>Year</option>
            {this.handleYear()}
          </select>
        </form>
      </>
    );
  }
}

export default Form;
